import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import FirebaseContext from '../context/firebase';
import * as ROUTES from '../constants/routes';
import { doesUsernameExist } from '../services/firebase';
export default function SignUp() {
    const {firebase}=useContext(FirebaseContext);
    const [username, setUsername] = useState('');
    const [fullName, setFullname] = useState('');
    const [emailAddress, setEmailAddress] = useState('');
    const [password, setPassword] = useState('');
    const[error,setError]=useState('');


    const isInvalid = password==''||fullName==''||emailAddress==''||username=='';
    useEffect(() => {
        document.title = 'Sign Up - Instagram';
    }, []);

    const handleSignUp= async(event)=>
    {
        const userNameExists =  doesUsernameExist(username);
        if(!userNameExists){ 
        event.PreventDefault();
        try{
            const createdUserResult=await firebase.auth().createUserWithEmailAndPassword(emailAddress,password)

            await createdUserResult.user.updateProfile({
                displayName:username
            })
            await firebase.firestore().collection('users').add({
                userId: createdUserResult.user.userId,
                username:username.toLowerCase(),
                fullName,
                emailAddress: emailAddress.toLowerCase(),
                following:[],
                followers:[],
                dateCreated: Date.now()
            })
        } catch(error)
        {
            setFullname('')
            setError(error.message);
        }
    }
    else{
        setFullname('')
            setPassword('')
            setEmailAddress('')
            setUsername('')
            setError(error.message);
    }
    }
    return (
        <div className="container flex mx-auto max-w-xs items-center h-screen">
            <div className="flex flex-col">
                <div className="flex flex-col items-center bg-white p-4 border mb-4">
                    <h1 className="flex justify-center w-full">
                       <img src="/images/logo.png" alt="Instagram" className="mt-2 w-6/12 mb-4" />
                    </h1>
                    {error && <p className="mb-4 text-xs text-red-500 text-center">{error}</p>}
                    <form onSubmit={handleSignUp} method="POST">
                        <input
                            aria-label="Enter your username"
                            className="text-sm text-gray w-full mr-3 py-5 px-4 h-2 border bg-gray-background rounded mb-2"
                            type="text"
                            placeholder="Username"
                            value={username}
                            onChange={({target})=>{setUsername(target.value)}}
                        />
                        <input
                            aria-label="Enter your full name"
                            className="text-sm text-gray w-full mr-3 py-5 px-4 h-2 border bg-gray-background rounded mb-2"
                            type="text"
                            placeholder="Full name"
                            value={fullName}
                            onChange={({target})=>{setFullname(target.value)}}
                        />
                        <input
                            aria-label="Enter your email address"
                            className="text-sm text-gray w-full mr-3 py-5 px-4 h-2 border bg-gray-background rounded mb-2"
                            type="text"
                            placeholder="Email address"
                            value={emailAddress}
                            onChange={({target})=>{setEmailAddress(target.value)}}
                        />
                        <input
                            aria-label="Enter your password"
                            className="text-sm text-gray w-full mr-3 py-5 px-4 h-2 border bg-gray-background rounded mb-2"
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={({target})=>{setPassword(target.value)}}
                        />
                        <button
                        disabled={isInvalid}
                            type="submit"
                            className={`bg-blue-500 text-white w-full rounded h-8 font-bold ${isInvalid && ' cursor-not-allowed opacity-50'}`}
                        >
                            Sign Up
                        </button>
                    </form>
                </div>
                <div className="flex justify-center items-center flex-col w-full bg-white p-4 border">
                    <p className="text-sm">
                        Have an account?{` `}
                        <Link to={ROUTES.LOGIN} className="font-bold text-blue">
                            Login
                        </Link>
                    </p>
                </div>
            </div>
        </div>
    )
}
