import React from 'react';
import { Route, Navigate } from 'react-router-dom';
import * as ROUTES from '../constants/routes';

export default function ProtectedRoute({ user, children, ...rest }) {

    if (user) {
        return <children />;
    }

    if (!user) {
        return (
            <Navigate replace
                to={{
                    pathname: ROUTES.LOGIN,
                    state: { from: location }
                }}
            />
        );
    }

}
